(30, 'habit-moment-survey', 'marketing', 'en_US', 'We\'re all ears! Share your experience so far with WooCommerce', 'We\'d love your input to shape the future of WooCommerce together. Feel free to share any feedback, ideas or suggestions that you have.', '{}', 'pending', 'woocommerce.com', '2022-01-04 07:47:21', NULL, 0, 'plain', '', 0, 'info'),
(31, 'wc-admin-complete-store-details', 'info', 'en_US', 'Add your store details to complete store setup', 'Complete your store details with important information for setup such as your stores base address', '{}', 'unactioned', 'woocommerce-admin', '2022-01-04 07:47:22', NULL, 0, 'plain', '', 0, 'info'),
(32, 'wc-refund-returns-page', 'info', 'en_US', 'Setup a Refund and Returns Policy page to boost your store\'s credibility.', 'We have created a sample draft Refund and Returns Policy page for you. Please have a look and update it to fit your store.', '{}', 'unactioned', 'woocommerce-core', '2022-01-04 07:47:22', NULL, 0, 'plain', '', 0, 'info'),
(33, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', '{}', 'unactioned', 'woocommerce-admin', '2022-01-04 07:47:22', NULL, 0, 'plain', '', 0, 'info'),
(34, 'wc-admin-set-up-additional-payment-types', 'info', 'en_US', 'Set up additional payment providers', 'Set up additional payment providers, using third-party services or other methods.', '{}', 'unactioned', 'woocommerce-admin', '2022-01-04 08:46:17', NULL, 0, 'plain', '', 0, 'info'),
(35, 'wc-payments-notes-set-up-refund-policy', 'info', 'en_US', 'Set up refund policy', 'Protect your merchant account from fraudulent disputes by defining the policy and making it accessible to customers.', '{}', 'unactioned', 'woocommerce-payments', '2022-01-04 08:46:52', NULL, 0, 'plain', '', 0, 'info'),
(36, 'wc-payments-notes-set-https-for-checkout', 'info', 'en_US', 'Force secure checkout', 'Protect your customers data and increase trustworthiness of your store by forcing HTTPS on checkout pages.', '{}', 'unactioned', 'woocommerce-payments', '2022-01-04 08:46:52', NULL, 0, 'plain', '', 0, 'info'),
(37, 'wc-payments-notes-multi-currency-available', 'info', 'en_US', 'Sell worldwide in multiple currencies', 'Boost your international sales by allowing your customers to shop and pay in their local currency.', '{}', 'unactioned', 'woocommerce-payments', '2022-01-04 08:46:52', NULL, 0, 'plain', '', 0, 'info'),
(38, 'wc-admin-learn-more-about-variable-products', 'info', 'en_US', 'Learn more about variable products', 'Variable products are a powerful product type that lets you offer a set of variations on a product, with control over prices, stock, image and more for each variation. They can be used for a product like a shirt, where you can offer a large, medium and small and in different colors.', '{}', 'unactioned', 'woocommerce-admin', '2022-01-04 08:52:45', NULL, 0, 'plain', '', 0, 'info'),
(39, 'wc-admin-filter-by-product-variations-in-reports', 'info', 'en_US', 'New - filter by product variations in orders and products reports', 'One of the most awaited features has just arrived! You can now have insights into each product variation in the orders and products reports.', '{}', 'unactioned', 'woocommerce-admin', '2022-01-05 11:28:06', NULL, 0, 'banner', 'http://localhost/spectrafilmandvideo/wp-content/plugins/woocommerce/packages/woocommerce-admin/images/admin_notes/filter-by-product-variations-note.svg', 0, 'info'),
(40, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Choosing a theme?', 'Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.', '{}', 'unactioned', 'woocommerce-admin', '2022-01-05 11:28:06', NULL, 0, 'plain', '', 0, 'info'),
(41, 'wc-admin-insight-first-product-and-payment', 'survey', 'en_US', 'Insight', 'More than 80% of new merchants add the first product and have at least one payment method set up during the first week.<br><br>Do you find this type of insight useful?', '{}', 'unactioned', 'woocommerce-admin', '2022-01-05 11:28:06', NULL, 0, 'plain', '', 0, 'info'),
(42, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2022-01-05 11:28:06', NULL, 0, 'plain', '', 0, 'info'),
(43, 'wc-admin-first-downloadable-product', 'info', 'en_US', 'Learn more about digital/downloadable products', 'Congrats on adding your first digital product! You can learn more about how to handle digital or downloadable products in our documentation.', '{}', 'unactioned', 'woocommerce-admin', '2022-01-05 11:28:07', NULL, 0, 'plain', '', 0, 'info'),
(44, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics  wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2022-01-06 08:45:28', NULL, 0, 'plain', '', 0, 'info'),
(45, 'ecomm-wc-navigation-survey', 'info', 'en_US', 'We\'d like your feedback on the WooCommerce navigation', 'We\'re making improvements to the WooCommerce navigation and would love your feedback. Share your experience in this 2 minute survey.', '{}', 'pending', 'woocommerce.com', '2022-01-06 08:45:29', NULL, 0, 'plain', '', 0, 'info'),
(46, 'storefront-customize', 'info', 'en_US', 'Design your store with Storefront ', 'Visit the Storefront settings page to start setup and customization of your shop.', '{}', 'unactioned', 'storefront', '2022-01-06 09:32:16', NULL, 0, 'plain', '', 0, 'info'),
(47, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Start accepting payments on your store!', 'Take payments with the provider thats right for you - choose from 100+ payment gateways for WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2022-01-11 12:41:24', NULL, 0, 'plain', '', 0, 'info'),
(48, 'wc-admin-woocommerce-payments', 'marketing', 'en_US', 'Try the new way to get paid', 'Securely accept credit and debit cards on your site. Manage transactions without leaving your WordPress dashboard. Only with <strong>WooCommerce Payments</strong>.<br><br>By clicking \"Get started\", you agree to our <a href=\"https://wordpress.com/tos/\" target=\"_blank\">Terms of Service</a>', '{}', 'actioned', 'woocommerce-admin', '2022-01-11 12:41:24', NULL, 0, 'plain', '', 0, 'info'),
(49, 'wc-admin-marketing-intro', 'info', 'en_US', 'Connect with your audience', 'Grow your customer base and increase your sales with marketing tools built for WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2022-01-11 12:41:24', NULL, 0, 'plain', '', 0, 'info'),
(50, 'wc-admin-marketing-jetpack-backup', 'marketing', 'en_US', 'Protect your WooCommerce Store with Jetpack Backup.', 'Store downtime means lost sales. One-click restores get you back online quickly if something goes wrong.', '{}', 'unactioned', 'woocommerce-admin-notes', '2022-01-11 12:41:24', NULL, 0, 'plain', '', 0, 'info'),
(51, 'wc-admin-insight-first-sale', 'survey', 'en_US', 'Did you know?', 'A WooCommerce powered store needs on average 31 days to get the first sale. You\'re on the right track! Do you find this type of insight useful?', '{}', 'unactioned', 'woocommerce-admin', '2022-01-11 12:41:24', NULL, 0, 'plain', '', 0, 'info'),
